import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/curve/Registry.sol";
import "../interfaces/curve/PoolInfo.sol";

contract CurveMulticall is Ownable {
    struct CurvePool {
        uint256[8] balances;
        uint256[8] underlyingBalances;
        uint256[8] decimals;
        uint256[8] underlyingDecimals;
        uint256[8] rates;
        address lpToken;
        bool isMeta;
        string name;
    }

    function firstPool(Registry registry)
        external
        view
        returns (address poolAddress)
    {
        poolAddress = registry.pool_list(0);
    }

    function getPoolInfo(PoolInfo poolInfo, address poolAddress)
        external
        view
        returns (
            uint256[8] memory balances,
            uint256[8] memory underlyingBalances,
            uint256[8] memory decimals,
            uint256[8] memory underlyingDecimals,
            uint256[8] memory rates,
            address lpToken,
            bool isMeta,
            string memory name
        )
    {
        (
            balances,
            underlyingBalances,
            decimals,
            underlyingDecimals,
            rates,
            lpToken,
            isMeta,
            name
        ) = poolInfo.get_pool_info(poolAddress);
    }

    function getPools(
        Registry registry,
        PoolInfo poolInfo,
        uint16 from,
        uint16 pageSize
    ) external view returns (CurvePool[] memory pools) {
        pools = new CurvePool[](pageSize);

        for (uint16 index = from; index < from + pageSize; index++) {
            address poolAddress = registry.pool_list(index);
            (
                uint256[8] memory balances,
                uint256[8] memory underlyingBalances,
                uint256[8] memory decimals,
                uint256[8] memory underlyingDecimals,
                uint256[8] memory rates,
                address lpToken,
                bool isMeta,
                string memory name
            ) = poolInfo.get_pool_info(poolAddress);

            pools[index - from] = CurvePool(
                balances,
                underlyingBalances,
                decimals,
                underlyingDecimals,
                rates,
                lpToken,
                isMeta,
                name
            );
        }
    }

    // Shouldn't hold funds, just in case.
    function recoverFunds(address to, address token) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        require(IERC20(token).transfer(to, balance), "Token recovery failed");
    }
}
