pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/wormhole/IWormhole.sol";

/// Responsible of charging users and emitting a message so that gas is sent to them in the destination chain.
contract GasBridgeCashier is Ownable {
    event SetChainId(uint16 indexed chainId, bool enabled);
    event SetTreasuryAddress(address indexed treasury);
    event SetGasTokenPrice(address indexed token, uint256 price);

    /// How many units of the gas token are required to bridge.
    mapping(address => uint256) gasTokenPrices;
    mapping(uint16 => bool) chainIds;
    IWormhole wormhole;
    address treasury;

    constructor(IWormhole _wormhole, address _treasury) {
        wormhole = _wormhole;
        treasury = _treasury;
    }

    receive() external payable {}

    function sendGas(
        bytes32 recipient,
        uint16 destinationChain,
        address paymentToken,
        uint32 nonce
    ) external returns (uint64 sequence) {
        require(chainIds[destinationChain], "Can't send to that chain id");

        require(gasTokenPrices[paymentToken] > 0, "Token not enabled");
        require(
            IERC20(paymentToken).transferFrom(
                msg.sender,
                treasury,
                gasTokenPrices[paymentToken]
            ),
            "Insufficient balance"
        );

        bytes memory payload = abi.encode(recipient, destinationChain);

        sequence = wormhole.publishMessage(nonce, payload, 1);
        return sequence;
    }

    function setGasTokenPrice(address token, uint256 value) external onlyOwner {
        gasTokenPrices[token] = value;
        emit SetGasTokenPrice(token, value);
    }

    function setChainIdEnabled(uint16 chainId, bool enabled)
        external
        onlyOwner
    {
        chainIds[chainId] = enabled;
        emit SetChainId(chainId, enabled);
    }

    function setTreasury(address _treasury) external onlyOwner {
        treasury = _treasury;
        emit SetTreasuryAddress(_treasury);
    }
}
