pragma solidity ^0.8.2;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "../interfaces/sushiswap/IMiniChefV2.sol";
import "../interfaces/uniswap/IUniswapV2Router02.sol";
import "../interfaces/uniswap/libraries/UniswapV2Library.sol";
import "./UniswapOperator.sol";
import "./interfaces/IRewarder.sol";
import "./interfaces/IRevoFarmBot.sol";

contract RevoOperator is UniswapOperator, Ownable {
  using SafeMath for uint256;
  using SafeERC20 for IERC20;

  IRevoFarmBot public immutable farmBot;
  IRewarder private rewarder;

  event Deposit(
    address indexed user,
    address token0,
    address token1,
    uint256 token0Amount,
    uint256 token1Amount,
    bool indexed swap
  );

  constructor(
    address _router,
    address _factory,
    address _farmBot,
    address _rewarder
  ) UniswapOperator(_router, _factory) {
    farmBot = IRevoFarmBot(_farmBot);
    rewarder = IRewarder(_rewarder);
  }

  receive() external payable {}

  function setRewarder(address _rewarder) public onlyOwner {
    rewarder = IRewarder(_rewarder);
  }

  function swapAndZapIn(
    address from,
    address to,
    uint256 fromAmount,
    uint256 minAmountOut,
    uint256 percentMin
  ) external {
    IERC20(from).safeTransferFrom(msg.sender, address(this), fromAmount);
    uint256 halfFromAmount = fromAmount / 2;
    uint256 toAmount = _swapUsingPool(from, to, halfFromAmount, minAmountOut);

    _zapIn(from, to, halfFromAmount, toAmount, percentMin, msg.sender);
    emit Deposit(msg.sender, from, to, halfFromAmount, toAmount, true);
  }

  function zapIn(
    address token0,
    address token1,
    uint256 token0Amount,
    uint256 token1Amount,
    uint256 percentMin
  ) external {
    IERC20(token0).safeTransferFrom(msg.sender, address(this), token0Amount);
    IERC20(token1).safeTransferFrom(msg.sender, address(this), token1Amount);

    _zapIn(token0, token1, token0Amount, token1Amount, percentMin, msg.sender);
    emit Deposit(msg.sender, token0, token1, token0Amount, token1Amount, false);
  }

  function _zapIn(
    address token0,
    address token1,
    uint256 token0Amount,
    uint256 token1Amount,
    uint256 percentMin,
    address user
  ) internal {
    _addLiquidityAndDepositInFarm(
      token0,
      token1,
      token0Amount,
      token1Amount,
      percentMin,
      user
    );

    rewarder.onReward(
      user,
      token0,
      token1,
      token0Amount,
      token1Amount,
      percentMin
    );

    // Return leftovers
    IERC20(token0).transfer(user, IERC20(token0).balanceOf(address(this)));
    IERC20(token1).transfer(user, IERC20(token1).balanceOf(address(this)));
  }

  function _addLiquidityAndDepositInFarm(
    address token0,
    address token1,
    uint256 token0Amount,
    uint256 token1Amount,
    uint256 percentMin,
    address user
  ) internal {
    _addLiquidity(token0, token1, token0Amount, token1Amount, percentMin);

    address lpToken = UniswapV2Library.pairFor(factory, token0, token1);
    uint256 lpBalance = IERC20(lpToken).balanceOf(address(this));
    IERC20(lpToken).approve(address(farmBot), lpBalance);
    farmBot.deposit(lpBalance);
    uint256 fpBalance = farmBot.balanceOf(address(this));
    farmBot.transfer(user, fpBalance);
  }
}
","keccak256":"0x54c271b702149a839b6f2bb2c4c6cd4b3934f4476303b9afb1a59e3f823a4be6","license":"MIT"},"contracts/defi/UniswapOperator.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "../interfaces/uniswap/IUniswapV2Router02.sol";
import "../interfaces/uniswap/IUniswapV2Pair.sol";
import "../interfaces/uniswap/libraries/UniswapV2Library.sol";

contract UniswapOperator {
  using SafeMath for uint256;

  address public immutable router;
  address public immutable factory;

  constructor(address _router, address _factory) {
    router = _router;
    factory = _factory;
  }

  function _swapUsingPool(
    address from,
    address to,
    uint256 inputAmount,
    uint256 minAmountOut
  ) internal returns (uint256 outAmount) {
    address pairAddress = UniswapV2Library.pairFor(factory, from, to);
    address[] memory path = new address[](2);
    path[0] = from;
    path[1] = to;

    require(
      IERC20(from).transfer(pairAddress, inputAmount),
      "UniswapOperator: Transfer failed"
    );
    IUniswapV2Pair pair = IUniswapV2Pair(pairAddress);

    (uint256 reserve0, uint256 reserve1, ) = pair.getReserves();
    if (pair.token0() == from) {
      outAmount = getAmountOut(inputAmount, reserve0, reserve1);
      require(
        outAmount >= minAmountOut,
        "UniswapOperator: Not enough output amount"
      );
      pair.swap(0, outAmount, address(this), new bytes(0));
    } else {
      outAmount = getAmountOut(inputAmount, reserve1, reserve0);
      require(
        outAmount >= minAmountOut,
        "UniswapOperator: Not enough output amount"
      );
      pair.swap(outAmount, 0, address(this), new bytes(0));
    }
  }

  function _addLiquidity(
    address token0,
    address token1,
    uint256 token0Amount,
    uint256 token1Amount,
    uint256 percentMin
  ) internal {
    IERC20(token0).approve(router, token0Amount);
    IERC20(token1).approve(router, token1Amount);
    IUniswapV2Router02(router).addLiquidity(
      token0,
      token1,
      token0Amount,
      token1Amount,
      (token0Amount * percentMin) / 100,
      (token1Amount * percentMin) / 100,
      address(this),
      block.timestamp
    );
  }

  function getAmountOut(
    uint256 amountIn,
    uint256 reserveIn,
    uint256 reserveOut
  ) internal pure returns (uint256 amountOut) {
    uint256 amountInWithFee = amountIn.mul(997);
    uint256 numerator = amountInWithFee.mul(reserveOut);
    uint256 denominator = reserveIn.mul(1000).add(amountInWithFee);
    amountOut = numerator / denominator;
  }
}
